isting record            {                // We edit the record                $pdir_db->db_Select("pd_department", "*", "pd_dept_id='$pd_dept_id'");                $pdir_row=$pdir_db->db_Fetch() ;                extract($pdir_row);                $pdir_cap1=phonedir_ADLAN_10 . " " . phonedir_ADLAN_76;                break;            }         case '2': // New department            {                // Create new record                $pd_dept_id=0;                 // set all fields to zero/blank                $pdir_cap1=phonedir_ADLAN_11 . " " . phonedir_ADLAN_76;                break;            }         case '3':            {                 // delete the record                if ($_REQUEST['pdir_okdel']=='1')                {                     // Check if category not used in any other table                    $pdir_countd=$pdir_db->db_Count("pd_directory", "(*)", " where pd_department='$pd_dept_id'");                    if ($pdir_countd==0)                    {                        $pdir_msg .=phonedir_ADLAN_54 . "<br />";                        if ($pdir_db->db_Delete("pd_department", " pd_dept_id='$pd_dept_id'"))                        {                            $pdir_text .="<table style='width:97%' class='fborder'><tr>						<td  class='fcaption' style='text-align: left;' colspan='2'>" . phonedir_ADLAN_12. "</td></tr>						<tr><td  class='forumheader3' style='text-align: left;' colspan='2'><strong>" . phonedir_ADLAN_81 . "</strong></td></tr>						<tr><td  class='fcaption' style='text-align: left;' colspan='2'><a href=''>" . phonedir_ADLAN_69 . "</a></td>						</tr></table>";                        }                         else                        {                            $pdir_text .="<table style='width:97%' class='fborder'><tr>						<td  class='fcaption' style='text-align: left;' colspan='2'>" . phonedir_ADLAN_12. "</td></tr>						<tr><td  class='forumheader3' style='text-align: left;' colspan='2'><s